definition.script:
pipeline {
    agent {
        label 'master'
    }
    parameters {
        string(name: 'aws_access_key_id', defaultValue: '', description: 'Access key for AWS:')
        string(name: 'aws_secret_access_key', defaultValue: '', description: 'Secret access key for AWS:')
        choice(name: 'selected_type_job', choices: ['Start'], description: 'Typed run job')
    }

    environment {
        PATH_TO_TERRAFORM_CONF = "/opt/TerraformAnsible/"
    }


    stages {
        stage('Start Main VM'){
            when { expression { return params.selected_type_job == 'Start' } }
                steps {
                    sh "export AWS_ACCESS_KEY_ID=${params.aws_access_key_id}"
                    sh "export AWS_SECRET_ACCESS_KEY=${params.aws_secret_access_key}"
                    sh "terraform -chdir ${env.PATH_TO_TERRAFORM_CONF} apply -auto-approve"
                }
        }
    }
}
